<section class="layout">
  <aside class="filters">
    <div class="field">
      <label>Search</label>
      <div class="search-row">
        <input type="search"
               placeholder="Search…"
               [ngModel]="searchTerm()"
               (ngModelChange)="searchTerm.set($event)" />
      </div>
    </div>

    <h2>Filters</h2>

    <!-- Movie filter -->
    <div class="field">
      <label>Movie</label>
      <select [ngModel]="selectedFilmUrl()" (ngModelChange)="selectedFilmUrl.set($event)">
        <option value="">All</option>
        <option *ngFor="let f of films()" [value]="f.url">
          Episode {{f.episode_id}} — {{f.title}}
        </option>
      </select>
    </div>

    <!-- Species filter -->
    <div class="field">
      <label>Species</label>
      <select [ngModel]="selectedSpeciesUrl()" (ngModelChange)="selectedSpeciesUrl.set($event)">
        <option value="">All</option>
        <option *ngFor="let s of species()" [value]="s.url">{{s.name}}</option>
      </select>
    </div>

    <!-- Birth year range -->
    <div class="field">
      <label>Birth Year Range</label>
      <div class="range-row">
        <input type="number"
               [ngModel]="minYear()"
               (ngModelChange)="minYear.set($event)"
               placeholder="Min (-30 = 30BBY)" />
        <span>to</span>
        <input type="number"
               [ngModel]="maxYear()"
               (ngModelChange)="maxYear.set($event)"
               placeholder="Max (5 = 5ABY)" />
      </div>
    </div>

    <button (click)="clearFilters()">Clear</button>
  </aside>

  <!-- Character list -->
  <section class="list-card">
    <div class="list-header">
      <h2>Characters</h2>
      <span>{{filtered().length}} of {{people().length}}</span>
    </div>

    <ul class="list">
      <li *ngFor="let p of filtered()" (click)="open(p)" class="item">
        <span>{{p.name}}</span>
        <span class="chip">{{p.birth_year}}</span>
      </li>
    </ul>
  </section>
</section>
