import { Component, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink } from '@angular/router';
import { SwapiService } from '../services/swapi.service';
import type { Person, Film, Species } from '../models/swapi.models';

@Component({
  selector: 'app-character-list',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterLink],
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.css']
})
export class CharacterListComponent {
  people = signal<Person[]>([]);
  films = signal<Film[]>([]);
  species = signal<Species[]>([]);

  selectedFilmUrl = '';
  selectedSpeciesUrl = '';
  minYear: number | null = null;
  maxYear: number | null = null;


 filtered = computed(() => {
  const term = this.searchTerm.toLowerCase().trim();
  return this.people().filter(p => {
    if (term && !p.name.toLowerCase().includes(term)) return false;
    if (this.selectedFilmUrl && !p.films.includes(this.selectedFilmUrl)) return false;
    if (this.selectedSpeciesUrl && !p.species.includes(this.selectedSpeciesUrl)) return false;
    const parsed = this.swapi.parseBirthYear(p.birth_year);
    if (this.minYear !== null && parsed !== null && parsed < this.minYear) return false;
    if (this.maxYear !== null && parsed !== null && parsed > this.maxYear) return false;
    if ((this.minYear !== null || this.maxYear !== null) && parsed === null) return false;
    return true;
  });
});


  constructor(private swapi: SwapiService, private router: Router) {
    this.swapi.getAllPeople().subscribe(all => {
      this.people.set(all);
      this.swapi.prefetchForFilters(all).subscribe(([films, species]) => {
        this.films.set([...films].sort((a,b)=>a.episode_id-b.episode_id));
        this.species.set([...species].sort((a,b)=>a.name.localeCompare(b.name)));
      });
    });
  }

  open(p: Person) {
    const id = this.swapi.idFromUrl(p.url);
    this.router.navigate(['/characters', id]);
  }

searchTerm = '';

clearFilters() {
  this.selectedFilmUrl = '';
  this.selectedSpeciesUrl = '';
  this.minYear = null;
  this.maxYear = null;
  this.searchTerm = '';
}
}

<section class="layout">
  <aside class="filters">
    <div class="field">
  <label>Search</label>
 <div class="search-row">
  <input type="search" placeholder="Search…" [(ngModel)]="searchTerm">
</div>

</div>

    <h2>Filters</h2>
    <div class="field">
      <label>Movie</label>
      <select [(ngModel)]="selectedFilmUrl">
        <option value="">All</option>
        <option *ngFor="let f of films()" [value]="f.url">
          Episode {{f.episode_id}} — {{f.title}}
        </option>
      </select>
    </div>

    <div class="field">
      <label>Species</label>
      <select [(ngModel)]="selectedSpeciesUrl">
        <option value="">All</option>
        <option *ngFor="let s of species()" [value]="s.url">{{s.name}}</option>
      </select>
    </div>

    <div class="field">
      <label>Birth Year Range</label>
      <div class="range-row">
        <input type="number" placeholder="-30=30BBY" [(ngModel)]="minYear">
        <span>to</span>
        <input type="number" placeholder="5=5ABY" [(ngModel)]="maxYear">
      </div>
    </div>

    <button (click)="clearFilters()">Clear</button>
  </aside>

  <section class="list-card">
    <div class="list-header">
      <h2>Characters</h2>
      <span>{{filtered().length}} of {{people().length}}</span>
    </div>



    <ul class="list">
      <li *ngFor="let p of filtered()" (click)="open(p)" class="item">
        <span>{{p.name}}</span>
        <span class="chip">{{p.birth_year}}</span>
      </li>
    </ul>
  </section>
</section>
